<style>
    .header {
        display: flex;
        flex-direction: row;
        justify-content: space-between;
        align-items: center;
        padding: 0.5rem 1rem;
        font-size: 1.2rem;
        font-weight: bold;
        position: sticky;
        top: 0;
        background-color: white;
        box-shadow: 0px 0.4rem 0.4rem white;
    }

    .header__beta {
        top: 0;
        left: 0;
        position: fixed;
        width: 3rem;
        height: 3rem;
    }

    .header__logo {
        height: 2rem;
    }

    .header__settings {
        position: absolute;
        top: 0.5rem;
        right: 0.5rem;
        width: 1.5rem;
        cursor: pointer;
        transition: all 0.2s ease-out;
    }
    .header__settings:hover {
        transform: rotate(145deg);
    }

    .empty-text-message {
        display: flex;
        justify-content: center;
        align-items: center;
        height: 100%;
        font-size: 1.2rem;
        border: 1px dashed black;
        padding: 1rem;
        text-align: center;
        min-height: 4rem;
        border-radius: 1rem;
        margin: 0.5rem;
